import React from 'react';
import Select from 'react-select';
import CreatableSelect from 'react-select/creatable';
import { useSelector, useDispatch, connect } from 'react-redux';
import { useLocation, useHistory } from 'react-router';
import { CUSTOM_STYLES, BULK_DATA_INSERT_TYPES } from '../../../config/globalConstant';
import LoadingIndicator from '../common/LoadingIndicator';
import { getAllOptions } from '../../../actions/assetActions';
import { bulkInsert } from '../../../actions/settingActions';



class BulkDataInsert extends React.Component {
    constructor(props) {
        super(props);
        
        this.state={
            type_of_data_to_insert: 0,
            type_options: [],
            bulk_data: "",
            bulk_loader:false
        }
        
    }
    componentDidMount() {
        this.props.getoptions();
        console.log(this.props.assetState);
        //this.setState({available_options})
    }
    componentWillUnmount() {
        console.log('componentWillUnmount');
    }
    onSelectDataType(e) {
        this.setState({type_of_data_to_insert: e.value});
    }
    render() {
        
        return (
            <section id="BulkDataInsert">
                <h4 className="mt-4 pt-5">
                    Bulk Data Insert
                </h4>
                <form id="bulkDataForm" name="bulkDataForm">
                <div className="my-2">
                    <div className="my-2">
                        Select Data want to insert:
                    </div>
                    <Select isLoading={this.state.bulk_loader} className="basic-single" classNamePrefix="select" id="type_of_data_to_insert" name="type_of_data_to_insert" options={BULK_DATA_INSERT_TYPES} onChange={e => this.onSelectDataType(e)} defaultValue={this.state.type_of_data_to_insert} components={{LoadingIndicator}} styles={CUSTOM_STYLES} />
                    <span id="type_of_data_to_insert_error"></span>
                </div>
                <div className="my-2">
                    <div className="my-2">
                        {BULK_DATA_INSERT_TYPES[this.state.type_of_data_to_insert].label}
                    </div>
                    <CreatableSelect isLoading={this.state.bulk_loader} formatCreateLabel={(inputValue) => 'Add or Create "'+inputValue+'"'} createOptionPosition="first" className="basic-single" classNamePrefix="select" id="who_can_contact" name="who_can_contact" options={props.all_options.who_can_contact_options} createOptionPosition={"first"} onChange={(value, actionMeta) => props.onSelectMultipleChange(value, actionMeta, 'who_can_contact', props.formikProps.values, props.formikProps.setFieldValue)} defaultValue={props.formikProps.values.who_can_contact} components={{LoadingIndicator}} styles={props.customStyles} />
                    <span></span>
                </div>
                </form>
            </section>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        assetState: state.assetState,
        settingState: state.settingState
    };
}

const mapDispatchToProps = dispatch => {
    return {
        getoptions: () => {
            dispatch(getAllOptions())
        },
        bulkInsert: () => {
            dispatch(bulkInsert())
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(BulkDataInsert);
