import React, { useEffect, useState } from 'react';
import { Formik, Form, ErrorMessage } from 'formik';
import { SET_SITE_LOADER, ALL_OPTIONS } from '../../../actions/constants'
import * as Yup from 'yup';
import Select from 'react-select';
import { useSelector, useDispatch }  from 'react-redux';
import Loader from '../../Loader';
import AssetSubForm from './AssetSubForm';

function AddAssets(props) {

    const [loading, setloader] = useState(true);

    const dispatch = useDispatch();
    //const loading = useSelector((state) => state.defaultReducers.site_loader);
    console.log(loading);
    const all_options = useSelector((state) => state.defaultReducers.all_options);;
    console.log('options', all_options);
    const type_of_asset_options_dom  = all_options.type_of_asset_options.map((type, index) => 
        <div className="form-check form-check-inline" key={index}>
            <input className="form-check-input" type="radio" name="type_of_asset" id={type.label} value={type.value} />
            <label className="form-check-label" htmlFor={type.label}>{type.label}</label>
        </div>
    );

    const asset_for_dom  = all_options.asset_for_options.map((type, index) => 
        <div className="form-check form-check-inline" key={index}>
            <input className="form-check-input" type="radio" name="asset_for" id={type.label} value={type.value} />
            <label className="form-check-label" htmlFor={type.label}>{type.label}</label>
        </div>
    );

    useEffect(() => {   
        //dispatch({type: SET_SITE_LOADER, payload: false});
        setloader(false);
    });

    const initialValues = {
        user_id: 0,
        type_of_asset: '',
        asset_for: 'Rent',
        asset_name: '',
        door_number: 0,
        street: '',
        nagar: '',
        city: 0,
        state: 0,
        landmark: '',
        age_of_asset: 0,
        age_of_asset_type: '',
        asset_main_image: {},
        asset_size_in_width_type: 'sq.ft.',
        asset_size_in_width: 0,
        asset_size_in_height_type: 'sq.ft.',
        asset_size_in_height: 0,
        asset_notes_by_owner: '',
        who_can_contact: [],
        advance_amount_for_asset:0,
        monthly_rent_for_asset: 0,
        lease_amount_for_asset: 0,
        lease_period_for_asset_type: '',
        asset_available_within: 0,
        asset_available_within_type: '',
        notice_period_for_vocate_asset: 0,
        notice_period_for_vocate_asset_type: '',
        asset_status_now: 0,
        show_my_asset_for_tenants: 'Show',
        number_of_rooms: 0,
        bhk: 0
    };

    const validationSchema = Yup.object().shape({
        type_of_asset: Yup.number()
            .required('Asset type is required'),
        asset_for: Yup.string()
            .required('Asset for is required'),
        asset_name: Yup.string()
            .required('Asset name is required'),
        door_number: Yup.number()
            .required('Door number is required'),
        city: Yup.number()
            .required('City is required'),
        state: Yup.number()
            .required('State is required'),
        asset_main_image: Yup.mixed()
            .required('Asset image is required')
    });

    const onSubmit = (values) => {
        console.log(values);
    };

    if(loading) {
        return (
            <div>
                <Loader />
            </div>
        );
    } else {
        return (
            <section id="AddAssets">
                <h3>Add Asset</h3>
                <div className="container">
                    <Formik initialValues={initialValues} validationSchema={validationSchema} onSubmit={onSubmit}>
                        {({ errors, values, field, touched, setValues, setFieldValue, handleChange, handleBlur }) => (
                            <Form>
                                <pre>
                                    {JSON.stringify(values)}
                                </pre>
                                <div className="form-group">
                                    <label htmlFor="">

                                    </label>
                                    <input type="text" className={'form-control' + (errors.type_of_asset && touched.type_of_asset ? ' is-invalid' : '')} id="type_of_asset" name="type_of_asset"  placeholder="Enter your first name" onChange={handleChange} onBlur={handleBlur} value={values.first_name}  />
                                </div>

                                <div className="mb-2 mt-2">
                                    {type_of_asset_options_dom}
                                </div>
                                <div className="mb-2 mt-2">
                                    {asset_for_dom}
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="asset_name" className="form-label">Asset_name</label>
                                    <input type="text" className={'form-control' + (errors.last_name && touched.last_name ? ' is-invalid' : '')} id="asset_name" name="asset_name" placeholder="Enter your last name" onChange={handleChange} onBlur={handleBlur} value={values.asset_name} />
                                    <ErrorMessage name="asset_name" component="span" className="text-danger" />
                                </div>
                                
                                <AssetSubForm asset_type={values.asset_name}></AssetSubForm>

                                

                                type_of_asset: 0,
                                asset_for: 'Rent',
                                asset_name: '',
                                door_number: 0,
                                street: '',
                                nagar: '',
                                city: 0,
                                state: 0,
                                landmark: '',
                                age_of_asset: 0,
                                age_of_asset_type: '',
                                asset_main_image: {},
                                asset_size_in_width_type: 'sq.ft.',
                                asset_size_in_width: 0,
                                asset_size_in_height_type: 'sq.ft.',
                                asset_size_in_height: 0,
                                asset_notes_by_owner: '',
                                who_can_contact: [],
                                advance_amount_for_asset:0,
                                monthly_rent_for_asset: 0,
                                lease_amount_for_asset: 0,
                                lease_period_for_asset_type: '',
                                asset_available_within: 0,
                                asset_available_within_type: '',
                                notice_period_for_vocate_asset: 0,
                                notice_period_for_vocate_asset_type: '',
                                asset_status_now: 0,
                                show_my_asset_for_tenants: 'Show',
                                number_of_rooms: 0,
                                bhk: 0
                            </Form>
                        )}
                    </Formik>
                </div>
            </section>
        );
    }
}

export default AddAssets;
