import {
    SITE_LOADER,
    TYPE_OF_ASSET_OPTIONS, 
    ASSET_FOR_OPTIONS,
    WHO_CAN_CONTACT_OPTIONS,
    SET_WHO_CAN_CONTACT_OPTIONS,
    ASSET_STATUS_NOW_OPTIONS,
    SHOW_MY_ASSET_FOR_TENANTS_OPTIONS,
    LIKE_OR_DISLIKE_OPTIONS,
    IS_PROPOSAL_CANCELLED_OPTIONS,
    ASSET_LENGTH_WIDTH_OPTIONS,
    ALL_OPTIONS,
    ALLOPT,
    USER_INFO,
    USER_LOADER,
    USER_LOGIN_FAIL,
    USER_LOGOUT,
    SIGNUP_FORM
} from '../actions/constants';


const user_info = localStorage.getItem('user_info')
  ? JSON.parse(localStorage.getItem('user_info'))
  : {}

const initialState = {
    site_loader: false,
    user_loader: false,
    user_info: user_info
};

export const userReducer = (state = initialState, action) => {
    switch (action.type) {
        case SITE_LOADER: {
            return {...state }
        }
        case SIGNUP_FORM: {
            return {...state, signup_form:action.payload }
        }
        case USER_LOADER: {
            return {...state, user_loader:action.payload }
        }
        case USER_INFO: {
            let alloptions = action.payload;
            return { ...state, alloptions }
        }
        default:
            return state;
    }
}
