import { applyMiddleware, createStore } from 'redux';
import { composeWithDevTools } from 'redux-devtools-extension';
import thunkMiddleware from 'redux-thunk';
import { rootReducer } from '../reducers'

let preloadedState;
const persistedTodosString = localStorage.getItem('state');
const storeOnToLocalStorage = ["INCREMENT"];


const saveLocal = ({getState}) => {
  return (next) => (action) => {
    const result = next(action);
    console.log('Middleware', action);
    if(storeOnToLocalStorage.indexOf(action.type)) {
      localStorage.setItem('state', JSON.stringify(getState()));
    }
    return result;
  };
}

console.log(persistedTodosString);

if(persistedTodosString) {
  preloadedState = JSON.parse(persistedTodosString)
}

export const store = createStore(rootReducer, preloadedState, composeWithDevTools(applyMiddleware(saveLocal, thunkMiddleware)));
