import React, { useEffect } from 'react';
import { Formik, Form, Field, FieldArray, ErrorMessage } from 'formik';
import { TYPE_OF_ASSET_OPTIONS, ASSET_FOR_OPTIONS, WHO_CAN_CONTACT_OPTIONS, ASSET_STATUS_NOW_OPTIONS, SHOW_MY_ASSET_FOR_TENANTS_OPTIONS, LIKE_OR_DISLIKE_OPTIONS, IS_PROPOSAL_CANCELLED_OPTIONS,  ASSET_HEIGHT_WIDTH_OPTIONS } from '../../../actions/constants'
import * as Yup from 'yup';
import Select from 'react-select';
import { connect, useSelector, useDispatch }  from 'react-redux';


const mapStateToProps = (state) => {
    return state;
};

function AddAssets(props) {
    console.log(props);
    /* const dispatch = useDispatch();
    dispatch({type:TYPE_OF_ASSET_OPTIONS});
    dispatch({type:ASSET_FOR_OPTIONS});
    dispatch({type:WHO_CAN_CONTACT_OPTIONS});
    dispatch({type:ASSET_STATUS_NOW_OPTIONS});
    dispatch({type:SHOW_MY_ASSET_FOR_TENANTS_OPTIONS});
    dispatch({type:LIKE_OR_DISLIKE_OPTIONS});
    dispatch({type:IS_PROPOSAL_CANCELLED_OPTIONS});
    dispatch({type:ASSET_HEIGHT_WIDTH_OPTIONS}); */

    /* const state = useSelector((state) => state);
    console.log(state); */

    

    const initialValues = {
        user_id: 0,
        type_of_asset: 0,
        asset_for: 'Rent',
        asset_name: '',
        door_number: 0,
        street: '',
        nagar: '',
        city: 0,
        state: 0,
        landmark: '',
        age_of_asset: 0,
        age_of_asset_type: '',
        asset_main_image: {},
        asset_size_in_width_type: 'sq.ft.',
        asset_size_in_width: 0,
        asset_size_in_height_type: 'sq.ft.',
        asset_size_in_height: 0,
        asset_notes_by_owner: '',
        who_can_contact: [],
        advance_amount_for_asset:0,
        monthly_rent_for_asset: 0,
        lease_amount_for_asset: 0,
        lease_period_for_asset_type: '',
        asset_available_within: 0,
        asset_available_within_type: '',
        notice_period_for_vocate_asset: 0,
        notice_period_for_vocate_asset_type: '',
        asset_status_now: 0,
        show_my_asset_for_tenants: 'Show',
        number_of_rooms: 0,
        bhk: 0
    };

    const validationSchema = Yup.object().shape({
        type_of_asset: Yup.number()
            .required('Asset type is required'),
        asset_for: Yup.string()
            .required('Asset for is required'),
        asset_name: Yup.string()
            .required('Asset name is required'),
        door_number: Yup.number()
            .required('Door number is required'),
        city: Yup.number()
            .required('City is required'),
        state: Yup.number()
            .required('State is required'),
        asset_main_image: Yup.mixed()
            .required('Asset image is required')
    });

    const onSubmit = (values) => {
        console.log(values);
    };

    return (
        <section id="AddAssets">
            <h3>Add Asset</h3>
            <div className="container">
                <Formik initialValues={initialValues} validationSchema={validationSchema} onSubmit={onSubmit}>
                    {({ errors, values, field, touched, setValues, setFieldValue, handleChange, handleBlur }) => (
                        <Form>
                            <pre>
                                {JSON.stringify(values)}
                            </pre>
                            <div className="form-group">
                                <label htmlFor="">

                                </label>
                                <input type="text" className={'form-control' + (errors.type_of_asset && touched.type_of_asset ? ' is-invalid' : '')} id="type_of_asset" name="type_of_asset"  placeholder="Enter your first name" onChange={handleChange} onBlur={handleBlur} value={values.first_name}  />
                            </div>
                            type_of_asset: 0,
                            asset_for: 'Rent',
                            asset_name: '',
                            door_number: 0,
                            street: '',
                            nagar: '',
                            city: 0,
                            state: 0,
                            landmark: '',
                            age_of_asset: 0,
                            age_of_asset_type: '',
                            asset_main_image: {},
                            asset_size_in_width_type: 'sq.ft.',
                            asset_size_in_width: 0,
                            asset_size_in_height_type: 'sq.ft.',
                            asset_size_in_height: 0,
                            asset_notes_by_owner: '',
                            who_can_contact: [],
                            advance_amount_for_asset:0,
                            monthly_rent_for_asset: 0,
                            lease_amount_for_asset: 0,
                            lease_period_for_asset_type: '',
                            asset_available_within: 0,
                            asset_available_within_type: '',
                            notice_period_for_vocate_asset: 0,
                            notice_period_for_vocate_asset_type: '',
                            asset_status_now: 0,
                            show_my_asset_for_tenants: 'Show',
                            number_of_rooms: 0,
                            bhk: 0
                        </Form>
                    )}
                </Formik>
            </div>
        </section>
    );
}

export default connect(mapStateToProps)(AddAssets);
