import React from 'react';
import { Field, FieldArray, ErrorMessage } from 'formik';
import Dropdown from 'react-dropdown';
import DatePicker from "react-datepicker";
import CreatableSelect from 'react-select/creatable';
import LoadingIndicator from '../common/LoadingIndicator';


function AssetAddFormWizzardStep7(props) {

    const handleNext = async () => {
        await props.formikProps.setTouched({['who_can_contact']:true, ['advance_amount_for_asset']:true, ['monthly_rent_for_asset']:true, ['lease_amount_for_asset']:true, ['lease_period_for_asset']:true, ['lease_period_for_asset_type']:true});
        console.log(props);
        if(props.formikProps.errors.who_can_contact == undefined && props.formikProps.errors.advance_amount_for_asset == undefined && props.formikProps.errors.monthly_rent_for_asset == undefined && props.formikProps.errors.lease_amount_for_asset == undefined && props.formikProps.errors.lease_period_for_asset == undefined && props.formikProps.errors.lease_period_for_asset_type == undefined) {
            props.nextStep();
        }
    };

    return (
        <div>
            
            <FieldArray
                name="rooms"
                render={arrayHelpers => (
                <div>
                    <button type="button" className="btn btn-primary" onClick={() => arrayHelpers.push({ asset_id:0, room_name: '', room_size_in_length:0, room_size_in_length_type:'sq.ft.', room_size_in_width:0, room_size_in_width_type:'sq.ft.', room_image:'' })} >Add Rooms</button>
                    {props.formikProps.values.rooms.map((room, index) => (
                        <div key={index}>
                            <div className="mb-2">
                                <label htmlFor={`rooms[${index}].room_name`}>Room Name</label>
                                <Field type="text" className="form-control" id={`rooms[${index}].room_name`} name={`rooms[${index}].room_name`} />
                            </div>

                            <div className="mb-2">
                                Room Length:
                            </div>
                            <div className="input-group mb-3">
                                <Field type="number" className="form-control" id={`rooms[${index}].room_size_in_length`} name={`rooms[${index}].room_size_in_length`} />
                                <Dropdown options={props.all_options.asset_length_width_options} id={`rooms[${index}].room_size_in_length_type`} name={`rooms[${index}].room_size_in_length_type`} onChange={e => props.onClickRoomsTypes(e, `rooms[${index}].room_size_in_length_type`, index, props.formikProps.values, props.formikProps.setFieldValue)} value={props.formikProps.rooms[index].room_size_in_length_type} placeholder="Select unit" />
                            </div>
                            
                            <div className="mb-2">
                                Room Width:
                            </div>
                            <div className="input-group mb-3">
                                <Field type="number" className="form-control" id={`rooms[${index}].room_size_in_width`} name={`rooms[${index}].room_size_in_width`} />
                                <Dropdown options={props.all_options.asset_length_width_options} id={`rooms[${index}].room_size_in_width_type`} name={`rooms[${index}].room_size_in_width_type`} onChange={e => props.onClickRoomsTypes(e, `rooms[${index}].room_size_in_width_type`, index, props.formikProps.values, props.formikProps.setFieldValue)} value={props.formikProps.room.room_size_in_width_type} placeholder="Select unit" />
                            </div>

                            <button type="button" className="btn btn-danger" onClick={() => arrayHelpers.remove(index)}>Remove</button>
                        </div>
                    ))}                                    
                </div>
                )}
            />

            <p>
                <button type="button" className="btn btn-success" onClick={handleNext}>Next Step</button>
                <button type="button" className="btn btn-info" onClick={props.previousStep}>Previous</button>
            </p>
        </div>
    );
}
export default AssetAddFormWizzardStep7;
