import React from 'react';
import Select from 'react-select';
import CreatableSelect from 'react-select/creatable';
import { ErrorMessage } from 'formik';

function AssetSubForm(props) {

    const onSelectCity = (e, field, values, setFieldValue) => {
        //console.log(e);
        if(e.__isNew__) {
            //Save and select
        } else {
            setFieldValue(field, e.value);
        }
    };

    if(props.asset_type == 'House') {
        return(
            <div>
                <h4>
                    {props.asset_type}
                </h4>
                <div className="my-2">
                    Address
                </div>
                <div className="my-2">
                    Door No.
                </div>
                <div className="input-group mb-3">
                    <span className="input-group-text" id="door_numbers">No.</span>
                    <input type="number" className="form-control max-wid-5" placeholder="Door Number"  id="door_number"  name="door_number" aria-label="Door Number" aria-describedby="basic-addon1" onChange={props.func.handleChange} onBlur={props.func.handleBlur} value={props.func.values.door_number} />
                </div>
                <div className="mb-3">
                    <label htmlFor="street" className="form-label">Street:</label>
                    <input type="text" className={'form-control' + (props.func.errors.street && props.func.touched.street ? ' is-invalid' : '')} id="street" name="street" placeholder="Enter street" onChange={props.func.handleChange} onBlur={props.func.handleBlur} value={props.func.values.street} />
                    <ErrorMessage name="street" component="span" className="text-danger" />
                </div>
                <div className="mb-3">
                    <label htmlFor="nagar" className="form-label">Nagar:</label>
                    <input type="text" className={'form-control' + (props.func.errors.nagar && props.func.touched.nagar ? ' is-invalid' : '')} id="nagar" name="nagar" placeholder="Enter street" onChange={props.func.handleChange} onBlur={props.func.handleBlur} value={props.func.values.nagar} />
                    <ErrorMessage name="nagar" component="span" className="text-danger" />
                </div>
                <div className="my-2">
                    <div className="my-2">
                        City:
                    </div>
                    <CreatableSelect formatCreateLabel={(inputValue) => 'Add or Create "'+inputValue+'"'} className="basic-single" classNamePrefix="select" id="city" name="city" options={props.options.city_options} onChange={e => onSelectCity(e, 'city', props.func.values, props.func.setFieldValue)} defaultValue={props.func.values.city} />
                </div>
                <div className="my-2">
                    <div className="my-2">
                        State:
                    </div>
                    <CreatableSelect formatCreateLabel={(inputValue) => 'Add or Create "'+inputValue+'"'} className="basic-single" classNamePrefix="select" id="state" name="state" options={props.options.state_options} onChange={e => onSelectCity(e, 'state', props.func.values, props.func.setFieldValue)} defaultValue={props.func.values.state} />
                </div>
                <div className="mb-3">
                    <label htmlFor="pincode" className="form-label">Pincode:</label>
                    <input type="number" className={'form-control' + (props.func.errors.pincode && props.func.touched.pincode ? ' is-invalid' : '')} id="pincode" name="pincode" placeholder="Enter pincode" onChange={props.func.handleChange} onBlur={props.func.handleBlur} value={props.func.values.pincode} />
                    <ErrorMessage name="street" component="span" className="text-danger" />
                </div>
                <div class="mb-3">
                    <label htmlFor="landmark" class="form-label">Landmark</label>
                    <textarea class="form-control" id="landmark" name="landmark" rows="3"  onChange={props.func.handleChange} onBlur={props.func.handleBlur} value={props.func.values.landmark}></textarea>
                </div>
                <div class="input-group mb-3">
                    <div class="dropdown">
                        <button class="btn btn-outline-secondary dropdown-toggle" type="button" id="dropdownMenuButton1" data-bs-toggle="dropdown" aria-expanded="false">Type</button>
                        <ul class="dropdown-menu" aria-labelledby="dropdownMenuButton1">
                            <li><span class="dropdown-item">Days</span></li>
                            <li><span class="dropdown-item">Months</span></li>
                            <li><span class="dropdown-item">Years</span></li>
                        </ul>
                    </div>
                    <input type="number" class="form-control" name="age_of_asset" id="age_of_asset" aria-label="Text input with dropdown button" />
                </div>
            </div>
        );
    } else if(props.asset_type == 'Appartment') {
        return(
            <div>
                <h4>
                    {props.asset_type}
                </h4>
                <div>

                </div>
            </div>
        );
    } else if(props.asset_type == 'Store') {
        return(
            <div>
                <h4>
                    {props.asset_type}
                </h4>
            </div>
        );
    } else if(props.asset_type == 'Koddon') {
        return(
            <div>
                <h4>
                    {props.asset_type}
                </h4>
            </div>
        );
    } else if(props.asset_type == 'Land') {
        return(
            <div>
                <h4>
                    {props.asset_type}
                </h4>
            </div>
        );
    }
}

export default AssetSubForm;
