import React, { useEffect } from 'react';
import RouterOutlet from './routes/RouterOutlet';
import { useDispatch, useSelector } from 'react-redux'
import { SET_SITE_LOADER } from './actions/constants';
import Loader from './components/Loader';

import './App.css';

function App() {
  const dispatch = useDispatch();
  dispatch({type: SET_SITE_LOADER, payload: true});
  const loading = useSelector((state) => state.site_loader);
  useEffect(() => {
    dispatch({type: SET_SITE_LOADER, payload: false});
  });
  if(loading) {
    return(
      <Loader />
    );
  } else {
    return (
      <div className="container">
          <RouterOutlet />
      </div>
    );
  }
}

export default App;
